uint64_t LidarSickSensorInfo::_computeHash(const __zcm_hash_ptr* p)
{
    const __zcm_hash_ptr* fp;
    for(fp = p; fp != NULL; fp = fp->parent)
        if(fp->v == LidarSickSensorInfo::getHash)
            return 0;
    const __zcm_hash_ptr cp = { p, (void*)LidarSickSensorInfo::getHash };

    uint64_t hash = (uint64_t)0xdf80f09c82044329LL +
         ZcmService::_computeHash(&cp);

    return (hash<<1) + ((hash>>63)&1);
}

